import{_ as i}from"./plugin-vue_export-helper-c27b6911.js";import{r as h,o as r,c,a as e,d as a,b as n,e as o}from"./app-fdd2faa4.js";const t={},l=e("h2",{id:"общая-концепция",tabindex:"-1"},[e("a",{class:"header-anchor",href:"#общая-концепция","aria-hidden":"true"},"#"),a(" Общая концепция")],-1),s=e("p",null,"Это небольшой гайд с несколькими идеями, которые должны помочь вам определиться с вашим персонажем. Сделать такого персонажа, что им не только будет интересно играть, но и он будет хорошо подходить для кампании. И в игре он будет сочетаться с персонажами других игроков.",-1),p=e("p",null,"Нет никакого определённого порядка в котором стоит придумывать персонажа. Лучше всего если по мере прочтения этой страницы вы будете держать в голове вольную концепцию персонажа, меняя её по мере прочтения пунктов. Так как каждый из них, может повлиять на остальные.",-1),u={class:"hint-container info"},f=e("p",{class:"hint-container-title"},"ФОРМАТ",-1),_={href:"https://docs.google.com/document/d/13UuxCi4r3zxdYp6HVfmIPfkt7QjE3-zQZbcZq_vnaMw/edit?usp=sharing",target:"_blank",rel:"noopener noreferrer"},x=o('<h2 id="происхождение" tabindex="-1"><a class="header-anchor" href="#происхождение" aria-hidden="true">#</a> Происхождение</h2><p>Происхождение — это то, что окружающие могут заметить в вас в первую очередь. Это вид (человек, дварф, орк) или культура (люди нерланцы или фероры отличаются друг от друга). Бэкграунд, социальный класс вашего персонажа. Ваш персонаж может быть типичным представителем своего происхождения или сильно выделяться среди подобных. Некоторые происхождения очень редки, другие же встречаются на каждом углу. В любом случае все происхождения подвержены стереотипам и они влияют на отношения к вам окружающих.</p><p>Кроме того, некоторые опции дают доступ к небольшим механическим изменениям персонажа или доступ к дополнительным языкам или физическим особенностям.</p><p>Для выбора происхождения для конкретной кампании стоит проконсультироваться с ведущим.</p><h2 id="класс" tabindex="-1"><a class="header-anchor" href="#класс" aria-hidden="true">#</a> Класс</h2><p>Класс — это умения вашего персонажа. Что он делает для достижения своих амбиций? Как он зарабатывает на жизнь? Классы выполняют так же важную миссию — воплощение фантазии игры персонажем, который принадлежит к какому-то архетипу. Персонаж который усиленно учиться, занимается формулами и исследованиями — волшебник или алхимик могут это реализовать. Персонаж — это угрюмый закалённый боец? Рэйнджер или воин могут быть отличным выбором.</p><p>Проконсультируйтесь с ведущим по поводу выбора класса подходящего для конкретной кампании (и с другими игроками, если вам хочется создать какую-то связь с ними на этапе создания персонажа).</p><h2 id="амбиции" tabindex="-1"><a class="header-anchor" href="#амбиции" aria-hidden="true">#</a> Амбиции</h2><p>Амбиции — это жизненные цели твоего персонажа, то, чего он хочет достичь. У всех персонажей есть <code>КРАТКОСРОЧНЫЕ</code> и <code>ДОЛГОСРОЧНЫЕ</code> амбиции.</p><h3 id="выбор-амбиции" tabindex="-1"><a class="header-anchor" href="#выбор-амбиции" aria-hidden="true">#</a> Выбор амбиций</h3><p>При выборе <code>АМБИЦИИ</code> следует проконсультироваться с ведущим. Он должен одобрить придуманную тобой <code>АМБИЦИЮ</code> и решить, будет ли она <code>КРАТКОСРОЧНОЙ</code> или <code>ДОЛГОСРОЧНОЙ</code>. Если ты захочешь изменить любую из имеющихся <code>АМБИЦИЙ</code>, можешь сделать это между игровыми встречами.</p><h3 id="краткосрочные-амбиции" tabindex="-1"><a class="header-anchor" href="#краткосрочные-амбиции" aria-hidden="true">#</a> Краткосрочные амбиции</h3><p><code>КРАТКОСРОЧНЫЕ АМБИЦИИ</code> — это ближайшие, самые достижимые из целей твоего персонажа. Если приложить некоторые усилия, то достичь их можно в течение нескольких недель, если не раньше — может даже дней. В большинстве случаев для реализации <code>КРАТКОСРОЧНОЙ АМБИЦИИ</code> требуется две-три игровые встречи.</p><p>Вот несколько примеров <code>КРАТКОСРОЧНЫХ АМБИЦИЙ</code>:</p><ul><li>разрушить репутацию соперника по романтическим отношениям.</li><li>отомстить за павшего товарища</li><li>найти учёного-затворника</li></ul><h3 id="долгосрочные-амибиции" tabindex="-1"><a class="header-anchor" href="#долгосрочные-амибиции" aria-hidden="true">#</a> Долгосрочные амибиции</h3><p><code>ДОЛГОСРОЧНЫЕ АМБИЦИИ</code> — это цели, ради достижения которых придётся упорно трудиться месяцами, а то и годами, и никто не гарантирует, что персонажу вообще удастся их достичь. <code>ДОЛГОСРОЧНЫЯ АМБИЦИЯ</code> вообще может служить персонажу скорее дополнительной жизненной мотивацией, нежели мечтой, которую он действительно сможет когда-нибудь осуществить. К примеру:</p><ul><li>стать владельцем замка</li><li>статься самым влиятельным магом Нитэля</li><li>основать новую страну</li></ul><div class="hint-container info"><p class="hint-container-title">Зловещие замыслы</p><p>Зловещие замыслы: о тайнах, или о неоднозначных с моральный точки зрения <code>АМБИЦИЯХ</code> твоего персонажа остальным игрокам знать не обязательно. Если ты, например, хочешь свергнуть правителя или собрать собственную армию оживших мертвецов, существует вероятность, что твои знакомые могут отнестись к этому несколько неодобрительно!</p></div><h3 id="реализация-амибиции" tabindex="-1"><a class="header-anchor" href="#реализация-амибиции" aria-hidden="true">#</a> Реализация амибиций</h3><p>Реализовав <code>КРАТКОСРОЧНУЮ АМБИЦИЮ</code>, персонаж получает 200 очков опыта и — в конце игровой встречи — возможность выбрать новую <code>КРАТКОСРОЧНУЮ АМБИЦИЮ</code>. Если персонаж реализует <code>ДОЛГОСРОЧНУЮ АМБИЦИЮ</code>, ты можешь выбрать одно из двух:</p><ul><li>Персонаж получает 1000 пунктов опыта и — в конце игровой встречи — возможность выбрать новую <code>ДОЛГОСРОЧНУЮ АМБИЦИЮ</code></li><li>герой отправляется на покой и становится персонажем ведущего, а ты моджешь создать нового персонажа, который получает &quot;премию&quot; в виде половины пунктов опыта, полученных предыдущим персонажем за всё время игры.</li></ul><p>Герой отправившийся на покой становится частью игрового мира, может помогать новым героям или давать им новые задания.</p><h3 id="общие-амибиции" tabindex="-1"><a class="header-anchor" href="#общие-амибиции" aria-hidden="true">#</a> Общие амибиции</h3><p>Не только персонаж имеет амбиции. Команада из всех персонажей или организация либо другая структура из игроков внутри кампании тоже имеет <code>КРАТКОСРОЧНУЮ АМБИЦИЮ</code> и <code>ДОЛГОСРОЧНУЮ АМБИЦИЮ</code>. Едиснтвенное различие между персональными абмициями и общими, что общие амбиции придумывают все персонажи совместно и награду за них получают все персонажи.</p><h2 id="связи-между-амбициями" tabindex="-1"><a class="header-anchor" href="#связи-между-амбициями" aria-hidden="true">#</a> Связи между амбициями</h2><p>Очень часто в драматических произведениях персонажи стремятся к чему-то и встречают на своём пути препятствия. После чего персонажи начинают что-то делать с этими препятствиями. Пользуясь этим приёмом, можно связать общие (долгосрочные амбиции) с персонажем. Если сделать достижение общих амбиций, для персонажа средством преодоления препятствия к личным амбициям.</p><p>К примеру:</p><ul><li>Персонаж хочет найти утерянный артефакт, когда-то принадлежавший его роду. Но город, где его видели, полон бандитов и воров. Обедневший аристократ не сможет просто заявиться и потребовать свой артефакт назад. В то время как общая амбиция команды — очистить город от коррупции и преступности совместно с капитаном стражи. Быть может именно так персонаж сможет вести свои поиски и расспросы.</li></ul><h2 id="оживляем-персонажа" tabindex="-1"><a class="header-anchor" href="#оживляем-персонажа" aria-hidden="true">#</a> Оживляем персонажа</h2><p>Один из полезных и очень удобных способов создания интересной биографии персонажа — ответить на ряд вопросов о нём и его прошлом. Не обязательно давать ответы на все вопросы, и эти ответы могут быть достаточно короткими — одно предложение или даже слово.</p><ol><li>Откуда ты?</li><li>Кто твои родственники?</li><li>Как прошло твоё детство?</li><li>Почему ты покинул дом?</li><li>Кто твои лучшие друзья?</li><li>Какова твоя заветная мечта?</li><li>Каково твоё наилучшее и наихудшее из воспоминаний?</li><li>Каковы твои религиозные убеждения?</li><li>Кому или чему ты хранишь верность?</li><li>Почему ты ищешь приключения?[^*]</li></ol><p>[^*]:Главный вопрос, именно ответ на него определяет почему этот персонаж вообще участвует в приключениях, а не живёт спокойной жизнью. Именно это поможет ведущему и игроку выбирать и отправляться на приключения.</p><h3 id="откуда-ты" tabindex="-1"><a class="header-anchor" href="#откуда-ты" aria-hidden="true">#</a> Откуда ты?</h3><p>Где родился и вырос твой персонаж? В одном из шумных городов Нитэля или в сонной деревеньке в глуши? На уединённой ферме вдали от цивилизации или в тёмных переулках? Под сводами неприступной горной твердыни или под шумом лесных деревьев? За названиями и деталями обратитесь к ведущему.</p><h3 id="кто-твои-родственники" tabindex="-1"><a class="header-anchor" href="#кто-твои-родственники" aria-hidden="true">#</a> Кто твои родственники?</h3><p>Чем занимались родители твоего персонажа, живы ли они? Есть ли у героя братья или сёстры? Был ли персонаж близок со своей семьёй? Одобряет ли семья то, чем персонаж занимается? Гордится ли она им или считает, что он пятнает родовую честь?</p><h3 id="как-прошло-твое-детство" tabindex="-1"><a class="header-anchor" href="#как-прошло-твое-детство" aria-hidden="true">#</a> Как прошло твоё детство?</h3><p>Был ли дом персонажа наполнен любовью, или это было холодное и негостиприимное местро? Пришлось ли персонажу с самого детсва заботиться о себе самостоятельно?</p><h3 id="почему-ты-покинул-дом" tabindex="-1"><a class="header-anchor" href="#почему-ты-покинул-дом" aria-hidden="true">#</a> Почему ты покинул дом?</h3><p>Персонаж до сих пор живёт в доме своего детства или уже давно покинул родовое гнездо? Может быть, его привлекли блеск и суета городской жизни? Может, он бежит от своего прошлого или пытается отыскать своё предназначение?</p><h3 id="кто-твои-лучшие-друзья" tabindex="-1"><a class="header-anchor" href="#кто-твои-лучшие-друзья" aria-hidden="true">#</a> Кто твои лучшие друзья?</h3><p>Родственников не выбирают, но друзья — другое дело. Сохранил ли персонаж связь с друзьями детства? Как он проводит с ними время? Пьянствует? Спорит на всевозможные темы? Быть может, персонаж водит близкую дружбу с кем-нибудь из персонажей других игроков? Или предпочитает животных людям?</p><h3 id="какова-твоя-заветная-мечта" tabindex="-1"><a class="header-anchor" href="#какова-твоя-заветная-мечта" aria-hidden="true">#</a> Какова твоя заветная мечта?</h3><p>Что за огонь пылает в сердце твоего персонажа? Конечно, у него есть амбиции, но это, как правило, что-то прозаическое, а то и вовсе банальное. Но если бы персонаж мог получить всё — абсолютно всё, что душе угодно, — чтобы бы он выбрал?</p><h3 id="каково-твое-наилучшее-и-наихудшее-из-воспоминании" tabindex="-1"><a class="header-anchor" href="#каково-твое-наилучшее-и-наихудшее-из-воспоминании" aria-hidden="true">#</a> Каково твоё наилучшее и наихудшее из воспоминаний?</h3><p>Каково самое драгоценное из воспоминаний твоего персонажа? Прекрасный и безмятежный летний вечер, купающийся в алых отсветах величественного заката? Или блистательная победа? А какое из воспоминаний персонажу по нраву меньше всего? Болезненное унижение? Расстроенный план? Ненависть, презрение или, хуже того, безразличие со стороны ближайшего окружения?</p><h3 id="каковыв-твои-религиозные-убеждения" tabindex="-1"><a class="header-anchor" href="#каковыв-твои-религиозные-убеждения" aria-hidden="true">#</a> Каковыв твои религиозные убеждения?</h3><p>Подавляющее большинство жителей этого мира — политеисты; существование и могущество богов для них — это факт, не подлежащий сомнению, а если боги существуют, значит, их надлежит почитать. Но, конечно, далеко не всех богов почитают одинаково. Является ли твой персонаж ярым приверженцем какого-то одного божества? За подробностями о богах обратись к ведущему.</p><h3 id="кому-или-чему-ты-хранишь-верность" tabindex="-1"><a class="header-anchor" href="#кому-или-чему-ты-хранишь-верность" aria-hidden="true">#</a> Кому или чему ты хранишь верность?</h3><p>Что для персонажа важнее всего? Друзья? Узы, что связывают его с кланом? Или герой — благочестивый верующий, для которого превыше всего заветы божества? Или превыше всего персонаж ценит собственную жизнь?</p><h3 id="почему-ты-ищешь-приключения" tabindex="-1"><a class="header-anchor" href="#почему-ты-ищешь-приключения" aria-hidden="true">#</a> Почему ты ищешь приключения?[^*]</h3><p>А вот и самый непростой вопрос: почему этот персонаж — персонаж игрока, а не ещё один простой житель этого мира? Что подвигло его ступить на стезю искателя приключений?</p><p>Персонаж выбрал дорогу приключений по собственной воле, в погоне за врагами или блеском богатств? Или герой был вынужден пойти на этот шаг в силу непреодолимых обстоятельств? Быть может, он ищет правосудия? Отмщения? Или героические странствия — это единственный способ искупить былые прегрешения и очистить от позора доброе имя? Или единственный шанс скрыться от неотступно преследующего героя врагов?</p>',54);function b(m,v){const d=h("ExternalLinkIcon");return r(),c("div",null,[l,s,p,e("div",u,[f,e("p",null,[a("Записывать детали персонажа можно воспользовавшись "),e("a",_,[a("этим документом!"),n(d)])])]),x])}const g=i(t,[["render",b],["__file","character_consept.html.vue"]]);export{g as default};
